syntax = "proto3";

message ConnectDefault1 {
  int64 ORDERTIME = 1;
  int64 ORDERID = 2;
  ConnectDefault2 ITEMID = 3;
  double ORDERUNITS = 4;
  repeated double ARRAYCOL = 5;
  repeated ConnectDefault4Entry MAPCOL = 6;
  ConnectDefault5 ADDRESS = 7;

  message ConnectDefault2 {
    int64 ITEMID = 1;
    string NAME = 2;
    ConnectDefault3 CATEGORY = 3;

    message ConnectDefault3 {
      int64 ID = 1;
      string NAME = 2;
    }
  }
  message ConnectDefault4Entry {
    string key = 1;
    double value = 2;
  }
  message ConnectDefault5 {
    int64 NUMBER = 1;
    string STREET = 2;
    string CITY = 3;
    string STATE = 4;
    int64 ZIPCODE = 5;
  }
}

